	parser.add_argument("--dir_gcat", type=str, help="input directory (same)")
	parser.add_argument("--input_name", type=str, help="name of input catalogs (same)")
	args = parser.parse_args()
	# config_file = str(args.config) # config file

	# lrg_LC_ABACUS(args)
